import React from 'react';

import './App.css';


//api.openweathermap.org/data/2.5/forecast?q=London,us&mode=xml
const API_key = "ab04fa8d5a7790709fea1bac8075ba7f";

class App extends React.Component{
  constructor(){
    super();
    this.state={}; 
    this.getWeather();
  }

  getWeather = async() =>{

    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=London,us&mode=xml&appid=${API_key}`);

    const response = await api_call.json();

    //console.log(response);
  };

  render(){
    return(
      <div className="App">
      <h1>Weather App</h1>
    </div>
    );
  }
}

export default App;
