class App extends React.Component{
  constructor(){
    super();
    this.state={
      city:undefined,
      country:undefined,
      icon:undefined,
      main:undefined,
      celcius:undefined,
      temp_max:undefined,
      temp_min:undefined,
      description:"",
      error:false
    };
    this.getWeather();
  }

  getWeather = async() =>{

    const api_call = await fetch(
      `http://api.openweathermap.org/data/2.5/forecast?q=London,us&appid=${API_key}`
    );

    const response = await api_call.json();

    console.log(response);
    console.log(response.list.length);
    const items = this.response.list.slice(0,7);
    //console.log(items);


    this.setState({
      city:response.city.name,
      country:response.city.country

    })
  };

  render(){
    return(
      <div className="App">
      <Weather city={this.state.city} 
      country={this.state.country} 
      temp_celius={this.state.celcius}
      temp_max={this.state.temp_max}
      temp_min={this.state.temp_min}
      description={this.state.description}

      />
      </div>
    );
  }
}

export default App;
