import React from 'react';
import {Route,BrowserRouter as Router,Link,NavLink } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Weather from  "./app_component/weather.component";


var moment = require('moment');



const API_key = "ab04fa8d5a7790709fea1bac8075ba7f";


// const routes = [
//   {
//     path: "/Day1",
//     component: Day1
//   },
//   {
//     path: "/Day2",
//     component: Day2,
//   },
//   {
//     path: "/Day3",
//     component: Day3,
//   },
//   {
//     path: "/Day4",
//     component: Day4,
//   },
//   {
//     path: "/Day5",
//     component: Day5,
//   }
// ];

const Day1 = ({id}) => {


  return(
      <div>
      <h1> Day1 Forecast{id.length}</h1>

      {id.map(function(i,index) {
      let newDate = new Date();
      const weekday = i.dt * 1000
      newDate.setTime(weekday)      
      const imgURL = `owf owf-${i.weather[0].id} owf-5x`
      return (
      <div className="col-sm-2">
      <div className="card">
        <h3 className="card-title">{moment(newDate).format('dddd')}</h3>
        <p className="text-muted">{moment(newDate).format('MMMM Do, h:mm a')}</p>
        <i className={imgURL}></i>
        <h2>{Math.round(i.main.temp)} °F</h2>
        <div className="card-body">
          <p className="card-text">{i.weather[0].description}</p>
        </div>
      </div>
      </div>)
          //<h1>{i.dt_txt},{i.main.temp}</h1>        
      })}
      </div>
    );
}

const Day2 = ({id}) => {
  return(
    <div>
    <h1> Day2 Forecast</h1>

    {id.map(function(i,index) {
        return <div><h1>{i.dt_txt},{i.main.temp},{i.weather[0].description}</h1> 
        <div className="card-body">
          <p className="card-text">{i.weather[0].description}</p>
        </div></div>       
    })}

    </div>
  );
}

const MainCard = ({id}) =>{
  let newDate = new Date();
  console.log(id);
  const weekday = id.dt * 1000;
  newDate.setTime(weekday);   
  const imgURL = `owf owf-${id.weather[0].id} owf-5x`
  return(
      <div>
        <h1> Day1 Forecast{id.length}</h1>
        <h1> Day1 Forecast{id.dt_txt}</h1>
        <div className="col-sm-2">
        <div className="card">
          <h3 className="card-title">{moment(newDate).format('dddd')}</h3>
          <p className="text-muted">{moment(newDate).format('MMMM Do, h:mm a')}</p>
          <i className={imgURL}></i>
          <h2>{Math.round(id[0].main.temp)} °F</h2>
          <div className="card-body">
            <p className="card-text">{id.weather[0].description}</p>
          </div>
        </div>
        </div>  
        </div>
  );
}


const WeatherDetailsCard = (id) =>{
  return(
    <div>
      <h1> Day1 Forecast{id[0].dt_txt}</h1>
      {id.map(function(i,index) {
      let newDate = new Date();
      const weekday = i.dt * 1000
      newDate.setTime(weekday)      
      const imgURL = `owf owf-${i.weather[0].id} owf-5x`
      return (
      <div className="col-sm-2">
      <div className="card">
        <h3 className="card-title">{moment(newDate).format('dddd')}</h3>
        <p className="text-muted">{moment(newDate).format('MMMM Do, h:mm a')}</p>
        <i className={imgURL}></i>
        <h2>{Math.round(i.main.temp)} °F</h2>
        <div className="card-body">
          <p className="card-text">{i.weather[0].description}</p>
        </div>
      </div>
      </div>)
          //<h1>{i.dt_txt},{i.main.temp}</h1>        
      })}
      </div>
  );
}


class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      items1:[],
      items2:[],
      city:undefined,
      country:undefined,
      icon:undefined,
      main:undefined,
      celcius:undefined,
      temp_max:undefined,
      temp_min:undefined,
      description:"",
      error:false
    };
    this.getWeather();
  }

  getWeather = async() =>{

    const api_call = await fetch(
      `http://api.openweathermap.org/data/2.5/forecast?q=London,us&appid=${API_key}`
    );

    const response = await api_call.json();

    console.log(response);
    console.log(response.list.length);
    // const items = response.list.slice(0,7);
    // console.log(items.length);

    this.setState({
      city:response.city.name,
      country:response.city.country,
      items1:response.list.slice(0,7),
      items2:response.list.slice(8,15),

    })
  };

  render(){
    return(


      <Router>
        <div className="App">
          <Link to="/day1"><MainCard id={this.state.items1[0]} /></Link>
          <Link to="/day2"><MainCard id={this.state.items2[0]} /></Link>

          <Route path="/day1" exact strict render={(props)=><Day1 id={this.state.items1}/>}/>
          <Route path="/day2" exact strict render={(props)=><Day2 id={this.state.items2}/>}/>
        </div>  
      </Router>  

      // <div className="App">
      // <Weather city={this.state.city} 
      // country={this.state.country} 
      // temp_celius={this.state.celcius}
      // temp_max={this.state.temp_max}
      // temp_min={this.state.temp_min}
      // description={this.state.description}
      // />
      // </div>
    );
  }
}

export default App;
